note_models.css:
.card {
    font-family: arial;
    font-size: 20px;
    text-align: center;
    color: black;
    background-color: white;
}

tmpls.qfmt:
{{#en_Bedeutung}}
	<div style='font-size: small; color: grey; text-align: right;'>{{Subdeck}}</div>

	<div style="font-size: x-large;">{{en_Bedeutung}}</div>

	{{#de_Synonyme}}
		<div style="font-size: medium; color: grey">Synonym(e): <span style="color: silver;">{{de_Synonyme}}</span></div>
	{{/de_Synonyme}}

	{{#en_Satz}}
	<br>
	<div style="color: lightgrey;">„{{en_Satz}}“</div>
	{{/en_Satz}}
{{/en_Bedeutung}}

